import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { todos } from 'src/app/Todo';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css'],
})
export class TodoItemComponent implements OnInit {
  @Input() todo: todos = new todos();
  @Output() todoDelete: EventEmitter<todos> = new EventEmitter();
  ngOnInit(): void {}

  onClick(todo: todos) {
    this.todoDelete.emit(todo);
    console.log('Delete Btn');
  }
  onCheckboxClick(): void{

  }
}
<div class="my-3">
  <h5 [class]="'nostrike'?todo.active:'strike'">{{ todo.title }}</h5>
  <p>{{ todo.desc }}</p>
  <div class="mb-3 form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1" (click)="onCheckboxClick(todo)" />
    <label class="form-check-label" for="exampleCheck1">Mark as done</label>
  </div>
  <button class="btn btn-sm btn-danger shadow-lg" (click)="onClick(todo)">
    Delete
  </button>
</div>
