import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { todos } from 'src/app/Todo';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css'],
})
export class TodoItemComponent implements OnInit {
  @Input() todo: todos = new todos();
  @Input() i: number | undefined;
  @Output() todoDelete: EventEmitter<todos> = new EventEmitter();
  @Output() todoCheckbox: EventEmitter<todos> = new EventEmitter();
  ngOnInit(): void {}

  onClick(todo: todos) {
    this.todoDelete.emit(todo);
    console.log('Delete Btn');
  }
  onCheckboxClick(todo: todos) {
    this.todoCheckbox.emit(todo);
    console.log('Checked ');
  }
}
<div class="my-3">
  <h5 [ngClass]="{ strike: !todo.active }">{{ todo.title }}</h5>
  <p [ngClass]="{ strike: !todo.active }">{{ todo.desc }}</p>
  <div class="mb-3 form-check">
    <input
      type="checkbox"
      class="form-check-input"
      id="todo{{ i }}"
      (click)="onCheckboxClick(todo)"
      [checked]="!todo.active"
    />
    <label class="form-check-label" for="todo">Mark as done</label>
  </div>
  <button class="btn btn-sm btn-danger shadow-lg" (click)="onClick(todo)">
    Delete
  </button>
</div>
