import { Component, OnInit } from '@angular/core';
import { todo } from 'src/app/todo';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css'],
})
export class TodosComponent implements OnInit {
  todos: todo[] = [];

  constructor() {
    this.todos = [
      {
        sno: 1,
        title: 'title1',
        desc: 'description1',
        active: true,
      },
      {
        sno: 2,
        title: 'title2',
        desc: 'description2',
        active: true,
      },
      {
        sno: 3,
        title: 'title',
        desc: 'description',
        active: true,
      },
    ];
  }
  ngOnInit(): void {}
}
<ul *ngFor="let todo of todos">
<li>{{ todo.sno }}- {{ todo.title }}- </li>
<li></li>
<li>{{ todo.desc }}</li>
</ul>
