import { JsonPipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { todos } from 'src/app/Todo';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css'],
})
export class TodosComponent implements OnInit {
  localItem: string | null;
  todos: todos[];
  constructor() {
    this.localItem = localStorage.getItem('todos');
    if (this.localItem == null) {
      this.todos = [];
    } else {
      this.todos = JSON.parse(this.localItem);
    }
  }
  ngOnInit(): void {}

  deleteTodo(todo: todos) {
    console.log(todo);
    const index = this.todos.indexOf(todo);
    this.todos.splice(index, 1);
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
  addTodo(todo: todos) {
    console.log(todo);
    this.todos.push(todo);
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
  toggleTodo(todo: todos) {
    const index = this.todos.indexOf(todo);
    this.todos[index].active = !this.todos[index].active
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
}
<app-todo-add (todoAdd)="[addTodo($event)]"></app-todo-add>
<div class="container rounded-3 border-2 border my-3 shadow-lg">
  <h4 class="text-center mt-3">Your Todos</h4>
  <div class="text-center my-3" *ngIf="this.todos.length === 0; else elseBlock">
    <h6>No todos To display <span class="fs-3">☹️</span> !</h6>
  </div>
  <ng-template #elseBlock>
    <div *ngFor="let todo of todos">
      <app-todo-item
        [todo]="todo"
        (todoDelete)="[deleteTodo($event)]"
        (todoCheckbox)="[toggleTodo($event)]"
      ></app-todo-item>
    </div>
  </ng-template>
</div>
