import { Component, OnInit } from '@angular/core';
import { todos } from 'src/app/Todo';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css'],
})
export class TodosComponent implements OnInit {
  todos: todos[];

  constructor() {
    this.todos = [
      {
        sno: 1,
        title: 'title1',
        desc: 'description',
        active: true,
      },
      {
        sno: 2,
        title: 'title2',
        desc: 'description2',
        active: true,
      },
      {
        sno: 3,
        title: 'title3',
        desc: 'description3',
        active: true,
      },
    ];
  }
  ngOnInit(): void {}

  deleteTodo(todo: todos) {
    console.log(todo);
    const index = this.todos.indexOf(todo);
    this.todos.splice(index, 1);
  }
}
<app-todo-add></app-todo-add>
.

<h4 class="container text-center mt-3">Your Todos</h4>

<div *ngFor="let todo of todos" class="container">
  <app-todo-item
    [todo]="todo"
    (todoDelete)="[deleteTodo($event)]"
  ></app-todo-item>
</div>
